import React from 'react';
import { useState } from 'react/cjs/react.development';
const Bill = () => {

    const [state,setState]= useState({products:[
        {title: 'Apple', count: 0, price: 100},
        {title: 'IBM', count: 0, price: 200},
        {title: 'HP', count: 0, price: 300},
      ]})





      console.log(state.products);
      const ProductList = ({ products, onChange }) => (
        <ul>
          {products.map((product, i) => (
            <li key={i}>
              {product.title}
              <input 
                type="text" 
                value={product.count}
                onChange={e => onChange(i, parseInt(e.target.value) || 0)}
              />
            </li>
          ))}
        </ul>
      );


      const onChange=(i,value)=>{
        //   console.log("index \t:"+i+"\n value :"+value);
        setState({products:state.products.map((product,index)=>{
            // return i===index ? {...products,count:value}:products
            console.log(product);
            console.log(index);
            console.log(i);
            console.log(value);
            console.log(i==product.index);

            return i===product.index ? {...product,count:value}:product;
        })})

      }


    return ( 
        <>
        <ProductList products={state.products} onChange={onChange} />
        </>
     );
}

export default Bill;
